// we are also importing the outlet here in order to get the home and shop below the nav-bar
import { Routes, Route, Outlet } from "react-router-dom";
import Home from "./components/routes/home/home.component";
import Navigation from "./components/routes/navigation/navigation.component";

const Shop = () => {
  return <h1>I am the shop</h1>;
};
const App = () => {
  return (
    <Routes>
      <Route path="/" element={<Navigation />}>
        <Route path="home" element={<Home />} />
        <Route path="shop" element={<Shop />} />
      </Route>
      {/* now above when we will write in url '/' then we will get Navigation only 
             for '/home' we will get the navigation as well as the home
             for '/shop' we will get the navigation as well as the shop */}

      {/* now what if we want to get the home as well as the navigation when we type 
             '/' only in url
             then we will have to use index instead of path what it does is if 
             there is nothing after '/' that is the path of parent then it will also render
             the element inside of index */}
      <Route path="/" element={<Navigation />}>
        <Route index element={<Home />} />
        <Route path="shop" element={<Shop />} />
      </Route>
      {/* the good thing about nesting is that we can do it indefinitely */}
    </Routes>
  );
};

export default App;
