import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: []
    };
  }

  componentDidMount(){
    //fetch is a promise 
    fetch('https://jsonplaceholder.typicode.com/users')
    // .then(response => console.log(response)) but this ain't in json format 
    .then((response) => response.json())
    //whatever is returned in response.json is being passed on to users 

    /*.then((users)=> console.log(users));*/
    // now let's get this into our state and ultimately monsters which can be done using setState

    .then((users)=> this.setState())
  }

  render() {
    return (
      <div className="App">
        {
          this.state.monsters.map((monster)=>{
           return(
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
           )
          })
        }
       </div>
    );
  }

}

export default App;
