import { Component } from "react";

class CardList extends Component{    
    render(){
        // setState triggers render
        // props also trigger render as whenever props change re-render 
        console.log('render from card-list');
        const {monsters} = this.props;
        return (
            <div classsName="card-list">
                {monsters.map((monster) => (
                    <div className="card-container">
                        {/* <img src="https://robohash.org/1?set=set2" alt={`monster ${monster.name}`} /> */}
                        <img src={`https://robohash.org/${monster.id}?set=set2`} alt={`monster ${monster.name}`} />
                        <h2>{monster.name}</h2>
                        <p>`${monster.email}</p>
                    </div>
                ))}
            </div>
        )
    }
}

export default CardList;
