import {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    /**calls the underlying constructor method from any other classes u're extending from */
    super();
    this.state ={
      name: 'Andrei'
    };
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          {/* whenever these variables change then react re renders the whole page again */}
          <p>Hi {this.state.name}</p>  
          <button onClick={()=> {
              /**this won't work as the react is reference by memory so it rerenders only when completely new object is created
               * while here only the value of same object is being altered 
               * react is reference to memory and these two objects are pointers pointing to the same memory
               * even though the value has changed it's still the same location in memory 
               * 
               * so react says that i'm going to re render only when their is a completely different object in memory
               * 
               * obj3 might look same as obj1 but it's not as obj3 is different as Object.assign is used to create new object
               * 
               * now this looks the same but it is different as we are typing it whole again
               * therefore it's not going to be equal to the obj1 or obj3
               * 
               * so here we are going to rather use setstate, we give it an object that we want to shallow merge with our current object 
               * shallow merge means whatever value u pass inside it if the key value is same then the value is going to be updated
               * 
               * 
               *  

              */
              this.state.name='Yihua';
              console.log(this.state);
              }  
            }
          >
            Change Name
          </button>
        </header>
      </div>
    );
  }
}

