import { Component } from "react";
import "./App.css";
import SearchBox from "./components/searchbox/search-box.component";
import CardList from "./components/card-list/card-list.component";


const App=() =>{

  onSearchChange= (event) => {
        /**now here searchField is not as it used to be in class
         * because setState is not here the same as it was in class 
         * 
         * so we have to do something new here 
         * 
         * using array destructuring
         * const arr = [2,4];
         * const [a,b]= arr;
         * a=2;
         * b=4;
         * 
         * 
        */
        const searchField = event.target.value.toLowerCase();
        this.setState(
          () => {
            return {searchField};
          }
        );
  }


  return(
    <div className="App">
      <div className="app-title">Monsters Rolodex</div>

      <SearchBox 
      onChangeHandler= {onSearchChange} /**now onSearchChange is function just like before */
      placeholder={'search monsters'}
      className= {'monster-search-box'}
      />
      {/* <CardList monsters={filteredMonster}/> */}
    </div>
  )
}

