import {Routes, Route} from 'react-router-dom'

import Home from "./components/routes/home.component";

const Shop = () => {
    return <h1>I am the shop</h1>
}
const App = () => {
    // return <Home/>;
    /**now anything that is routable is going to come inside the Routes component */
    return(
        <Routes>
          <Route path = '/' element={<Home/>}>
                 <Route path='shop' element={<Shop/>}/>
             </Route>
            {/* now what happens here is it makes the path insider relative to 
            the path of the parent
            so for inside route path to run it first has to match the path of the parent and then 
            of child like ===>>> '/shop'
            if the path of the parent was '/home' instead of '/' then we have to write 
            '/home/shop' in url in order to do the matching */}

            {/* TWIST
            the thing doesn't work out as told
            even on writing  /home/shop in url
            because the parent /home is still being rendered
            amd we haven't told that where the matching /shop component must go*/}

            {/* so now in order to tell where our matching component /shop must go
            we need to go into the code of parent component 
            import outlet and tell where we want that outlet to be after 
            putting the directory into parent div */}

        </Routes>
    )
};

export default App;
