import { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: "Andrei",
      company: "ZTM",
    };
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Hi this is {this.state.name}, i work at {this.state.company}</p>
          {/* the reason for the console log still showing the previous value is that this runs asynchronously so while console.log runs the name */}
          <button
            onClick={() => {

              this.setState({ name: "Yihua" });
              console.log(this.state);
            }}
          >
            Change Name
          </button>
        </header>
      </div>
    );
  }
}

export default App;
