import Button from "../button/button.component";
import { 
    signInWithGooglePopup, 
    createUserDocumentFromAuth,
    signInAuthUserWithEmailAndPassword } from "../utils/firebase/firebase.utils";
import { useState  } from "react";
import FormInput from "../form-input/form-input.component";
import './sign-in-form.styles.scss'
const defaultFormFields = {
    email: '',
    password: '',
}

const SigniInForm = () => {
    const[formFields, setFormFields] = useState(defaultFormFields);
    const {email, password} = formFields;

    console.log(formFields);

    const resetFormFields = () => {
        setFormFields (defaultFormFields);
    }

    const signInWithGoogle = async ()=>{
        const {user} = await signInWithGooglePopup();
        const userDocRef = createUserDocumentFromAuth(user);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();


        try {
          const response = await signInAuthUserWithEmailAndPassword(email,password)
          console.log(response);
          resetFormFields();
        } catch (error) {
            /*// for wrong password
            if(error.code==="auth/wrong-password"){
                alert('incorrect password for email')
            }
            //for wrong email
            else if(error.code==="auth/user-not-found"){   

            }*/
            //rather then if nested we can use switch
            switch(error.code){
                case 'auth/wrong-password':
                    alert('incorrect password for email');
                    break;
                case 'auth/user-not-found':
                        alert('no user associated with this email');
                        break; 
                default:
                    console.log(error);
            }
            console.log(error);
        }
      };

    /**
     * handleChange will remain same in signin as was in signup
     */
    const handleChange = (event) => {
        const {name, value} = event.target;
        setFormFields({...formFields, [name]:value});
    }
    return(
        <div className="sign-in-container">
            <h2>Already have an account?</h2>
            <h1> Sign in with your email and password</h1>
            <form onSubmit= {handleSubmit}>
                <FormInput 
                 label='Email'
                 type="email" 
                 onChange={handleChange} 
                 name="email" value={email} 
                 required 
                 />
                <FormInput 
                 label='Password'
                 type="password" 
                 onChange={handleChange} 
                 name="password" 
                 value={password} 
                 required 
                 />
                 <div className="buttons-container">
                <Button type="submit">Sign In</Button>
                {/* by default the type of button is submit so even when the email is wrong it submits before showing the error 
                like rather than submitting using */}
                <Button type='button' buttonType='google' onClick= {signInWithGoogle}>Google Sign In</Button>
                 </div>
            </form> 
        </div>
    ) 
}

export default SigniInForm;
