import { useEffect, useState } from "react";
import "./App.css";
import SearchBox from "./components/searchbox/search-box.component";
import CardList from "./components/card-list/card-list.component";

const App=() =>{
  console.log('render');
  const[searchField, setSearchField] = useState('');
  const[monsters,setMonsters] = useState([]);
  const [filteredMonster,setFilteredMonster] = useState(monsters); 

  const onSearchChange= (event) => {
      const searchFieldString = event.target.value.toLowerCase();
      setSearchField(searchFieldString);
  }


useEffect(()=>{
  console.log('effect fired');
  fetch("https://jsonplaceholder.typicode.com/users")
    .then((response) => response.json())
    .then((users) => setMonsters(users));
}, []);

/**
 * next big thing is filteredMonster
 * now everytime this function runs this filteredMonster array 
 * is getting re-built 
 * 
 * it's ok like we want this to happen whenever monster or searchField 
 * change 
 * 
 * say for example we also want it to trigger some changes 
 */
useEffect(()=>{
  const newFilteredMonster = monsters.filter((monster) => {
    return monster.name.toLowerCase().includes(searchField); 
  });
  setFilteredMonster(newFilteredMonster)

  console.log('Effect is firing');
}, [ monsters, searchField]) /** this dependency array here suggests that to run the call back function only when these values change so now on changing the string the effect won't be firing */

/**it is taken above only */
  // const filteredMonster = monsters.filter((monster) => {
  //   return monster.name.toLowerCase().includes(searchField); 
  // });

  console.log(filteredMonster);
  return(
    <div className="App">
      <div className="app-title">Monsters Rolodex</div>

      <SearchBox 
        onChangeHandler= {onSearchChange} 
        placeholder={'search monsters'}
        className= {'monster-search-box'}
      />
      {/* for example */}
      <SearchBox 
        onChangeHandler= {onStringChange} 
        placeholder={'set string'}
      />
      <CardList monsters={filteredMonster}/>
    </div>
  )
}

