import {createContext,useState} from 'react'

//as the actual value u wanna access
export const UserContext = createContext({
    /**
     * currentUser being an actual object the empty state of
     * an object should be null as we want a  null check to define whether or not 
     * we have a user existing object or no object
     */
    currentUser: null,
    /**
     * it;'s an empty function that does nothing 
     * we are going to set it in the app component itself 
     * as when and how we will require the currentUser 
     */
    setCurrentUser: () => null,
});
export const UserProvider = ({children}) => {
   const [currentUser, setCurrentUser] = useState(null);
   const value = {currentUser, setCurrentUser};  
   return <UserContext.Provider value= {value}> {children}</UserContext.Provider>
}
