import { Component } from "react";
import "./App.css";
class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [],
      searchField: '',
    };
  }

  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) => {
        this.setState(
          () => {
            return { monsters: users };
          },
          () => {
            console.log(this.state);
          }
        );
      });
  }

  onSearchChange = (event) => {
    console.log({ startingArray: this.state.monsters });
    const searchField = event.target.value.toLowerCase();
    this.setState(searchField);
  }

  render() {

        const {monsters,searchField} = this.state;
        const{onChange} =this;


    const filteredMonster = this.state.monsters.filter((monster) => {
      return monster.name.toLowerCase().includes(this.state.searchField); 
    });

    return (
      <div className="App">
        <input
          className="search-box"
          type="search"
          placeholder="search monsters"
          //this here as it is attached to our class now 
          onChange={this.onSearchChange}
          // this is optimisation because the class component App as usual, has to make this function again
          // but now when it's called once this class will remember it as it is a method and won't build again 
          // whenever required 

        />
        {filteredMonster.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
              );
        })}
      </div>
    );
  }
}
export default App;
