import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: []
    };
    // console.log('1');
  }

  componentDidMount(){
    // console.log('3'); 
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users)=>{
      this.setState(
        () =>{
          return {monsters: users}
        },
        () => {
          console.log(this.state)
        }
      )
    })
  }

  render() {
    // console.log('2');
    return (
      <div className="App">
        {/* these tags like input,div,h1 may look like a html tag but these are all defined in the react components  */}
        <input className="search-box" type="search" placeholder="search monsters" onChange={(event)=>{
          // console.log(event);                           syntheticBaseEvent; in it's target value will come the text we have entered in the search box in this property their is '_reactName: onChange' which suggest that this is a react event 
            this.state.monsters.filter

        }}/>
        {
          this.state.monsters.map((monster)=>{
           return(
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>
           )
          })
        }
       </div>
    );
  }

}

export default App;
