/**
 * so what we want for the currentUser
 * is instead of sign In we want to show Sign Out 
 * if the user is signed in
 */
import { Fragment,useContext } from "react";
import {Outlet, Link} from 'react-router-dom';
import {ReactComponent as CrwnLogo} from "../../../assets/crown.svg"
import './navigation.styles.scss'
import {UserContext} from '../../../context/user.context'
/**
 * importing signOut from firebase utils to actually sign the user OUT
 */
import { signOutUser } from "../../utils/firebase/firebase.utils";
const Navigation = () =>{
    const {currentUser, setCurrentUser} = useContext(UserContext);
    console.log(currentUser);

    /**
     * we are making it async in order to await
     * what the signOut user returns from firebase utils
     */
    const signOutHandler = async() =>{
        const res= await signOutUser();
        setCurrentUser(nill)
    }
    return(
        <Fragment>
            <div className='navigation'>
                <Link className= 'logo-containert' to='/'>
                    <CrwnLogo className='logo'/>
                </Link>
            <div className='nav-links-container'>
                <Link className= 'nav-link' to='/shop'>
                    SHOP
                </Link>
                {/* 
                here we are going to make changes for sign out 
                 */}
                { currentUser ? (
                        /**
                         * we are giving the className as nav-link so as to inherit all the 
                         * properties of the nav bar things styling
                         */

                        // <span className="nav-link" onClick={signOutUser}>SIGN OUT</span>

                        /**
                         * there is a problem with directly signing out 
                         * as our context is not yet hooked up to this signOut 
                         */
                        <span className="nav-link" onClick={signOutHandler}>SIGN OUT</span>
                        //if the user is not signed in then show the link for SIGN IN
                        ):( 
                        <Link className="nav-link" to='/auth'>
                            SIGN IN
                        </Link>
                        )
                }
                {/* <Link className= 'nav-link' to='/auth'>
                    SIGN IN
                </Link> */}
            </div>
            </div>
            <Outlet/>
        </Fragment>
    )
}

export default Navigation;
