import { Component } from "react";
// import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      // we will make an array of monsters and removed the key values monster1, 2 and 3 as it's array
      monsters: [
        {
          name: 'Linda'
        },
        {
          name: 'Frank'
        },
        {
          name: 'Jacky'
        },
        {
          name: 'Andrei'
        }
      ]
    };
  }
  render() {
    return (
      <div className="App">
        {/* now we want to access the array above and convert each of them into h1's */}
        {
          this.state.monsters.map((monster)=>{
            return <h1 key={monster}>{monster.name}</h1>
          })
        }
        {/* next we will resolve the warning that says each child in a list should have a unique key property */}
      </div>
    );
  }

}

export default App;
