import { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      monsters: [
        {
          name: 'Linda',
          id: '123'
        },
        {
          name: 'Frank',
          id: '123a'
        },
        {
          name: 'Jacky',
          id: '123b'
        },
        {
          name: 'Andrei',
          id: '123c'
        }
      ]
    };
  }
  render() {
    return (
      <div className="App">
        {
          this.state.monsters.map((monster)=>{

            // return <h1 key={monster.name}>{monster.name}</h1>
            /* key is the unique value and when we have many records name is not 
            * going to be unique
            */

            // return <h1 key={monster.id}>{monster.name}</h1>
            /* now why the key is needed?
             * without key how the react will differentiate linda from frank, jacky and andrei
             * let's say u want to change jacky to yihua 
             * now without key react re-render all the h1's 
             * with key only jacky changed to yihua 
             * 
             * now let's write in a much better way that is within div 
             * so as to differentiate them much better
            */
            div
          })
        }
       </div>
    );
  }

}

export default App;
