import { Component } from "react";
import './'
class SearchBox extends Component{
    render() {
        return(
            <input
            /**we want className to be dynamic and generalised 
             * as for every searchbox we don't want it's class to be search-box
             * similarly for placeholder so for now let's comment them
             */
            // className="search-box"
            // placeholder="search monsters"

            /**type must remain search only as to give us that particular search box */
            type="search"
            onChange={this.props.onChangeHandler}
            placeholder={this.props.placeholder}
            className={`search-box {this.props.className}`}
          />
        )
    }
}

export default SearchBox;
