import { useState } from "react";
import "./App.css";
import SearchBox from "./components/searchbox/search-box.component";
import CardList from "./components/card-list/card-list.component";

const App=() =>{
  console.log('render');
  const[searchField, setSearchField] = useState('');

  const onSearchChange= (event) => {
      const searchFieldString = event.target.value.toLowerCase();
      setSearchField(searchFieldString);
  }

  const[monsters,setMonsters] = useState([]);


    fetch("https://jsonplaceholder.typicode.com/users")
      .then((response) => response.json())
      .then((users) => {
        setMonsters(
          // () => {
          //   return { monsters: users };
          // }
          /**
           * now instead of passing in the function 
           * let's just pass the value 
           */
          users
        );
      });

  // now where to get the monsters from? 
  /**
   * earlier this monster was in our state which was initialised as
   * an empty array 
   * 
   * so above we have done the thing needed we have put an empty array
   * just like earlier but for this we have to create a new 
   * useState as it can has only one value 
   * not like the object that could take multiple values in the 
   * setState 
   *
   * alright so once we have got the empty array how to fill in the values
   * into it 
   * 
   * like earlier we did componentDidMount which used fetch to take
   * all the data from the url once only 
   * 
   * so let's put that fetch over here 
   */

  /**
   * now their is a problem with this fetch because 
   * the fetching of data is done from an external place that is website 
   * now though the values are same within memory it's different each time 
   * therefore renders and re
   */
  const filteredMonster = monsters.filter((monster) => {
    return monster.name.toLowerCase().includes(searchField); 
  });

  return(
    <div className="App">
      <div className="app-title">Monsters Rolodex</div>

      <SearchBox 
        onChangeHandler= {onSearchChange} 
        placeholder={'search monsters'}
        className= {'monster-search-box'}
      />
      <CardList monsters={filteredMonster}/>
    </div>
  )
}

